$(function(){
		//province;
		//prohospital;
		//医院科室名称 激活状态
		$("#hospitalName").focus(function(){
		  var top = $(this).position().top+22;
    	  var left = $(this).position().left;
    	  $("div[class='provinceHospital']").css({top:top,left:left});
    	  $("div[class='provinceHospital']").show();
		});
		//初始化省下拉框
		var provinceArray = "";
		var provicneSelectStr = "";
		for(var i=0,len=province.length;i<len;i++){
		  provinceArray = province[i];
		  provicneSelectStr = provicneSelectStr + "<option value='"+provinceArray[0]+"'>"+provinceArray[1]+"</option>"
		} 
		$("div[class='proSelect'] select").html(provicneSelectStr);
		//初始化城市列表
		var selectPro = $("div[class='proSelect'] select").val();
		var schoolUlStr = "";
		var hospitalListStr = new String(prohospital[selectPro]);
		var hospitalListArray = hospitalListStr.split(",");
		var temphospitalName = "";
		for(var i=0,len=hospitalListArray.length;i<len;i++){
		  temphospitalName = hospitalListArray[i];
		  //console.log(temphospitalName.length);
		  if(temphospitalName.length>13){
		  	schoolUlStr = schoolUlStr + "<li class='DoubleWidthLi'>"+hospitalListArray[i]+"</li>"
		  }else {
		  	schoolUlStr = schoolUlStr + "<li>"+hospitalListArray[i]+"</li>"
		  }
		}
		$("div[class='hospitalList'] ul").html(schoolUlStr);
		//省切换事件
		$("div[class='proSelect'] select").change(function(){
		  if("99"!=$(this).val()){
		    $("div[class='proSelect'] span").show();
		    $("div[class='proSelect'] input").hide();
		  	schoolUlStr = "";
		    hospitalListStr = new String(prohospital[$(this).val()]);
		    hospitalListArray = hospitalListStr.split(",");
		    for(var i=0,len=hospitalListArray.length;i<len;i++){
		  	  temphospitalName = hospitalListArray[i];
			  if(temphospitalName.length>13){
			  	schoolUlStr = schoolUlStr + "<li class='DoubleWidthLi'>"+hospitalListArray[i]+"</li>"
			  }else {
			  	schoolUlStr = schoolUlStr + "<li>"+hospitalListArray[i]+"</li>"
			  }
		    }
		    $("div[class='hospitalList'] ul").html(schoolUlStr);
		  }else {
		    $("div[class='hospitalList'] ul").html("");
		    $("div[class='proSelect'] span").hide();
		    $("div[class='proSelect'] input").show();
		  }
		});
		//医院列表mouseover事件
		$("div[class='hospitalList'] ul li").live("mouseover",function(){
		  $(this).css("background-color","#72B9D7");
		});
		//医院列表mouseout事件
		$("div[class='hospitalList'] ul li").live("mouseout",function(){
		  $(this).css("background-color","");
		});
		//医院列表点击事件
		$("div[class='hospitalList'] ul li").live("click",function(){
		  $("#hospitalName").val($(this).html());
		  $("div[class='provinceHospital']").hide();
		});
		//按钮点击事件
		$("div[class='button'] button").live("click",function(){
		  var flag = $(this).attr("value");
		  if("0"==flag){
		    $("div[class='provinceHospital']").hide();
		  }else if("1"==flag){
		    var selectPro = $("div[class='proSelect'] select").val();
		    if("99"==selectPro){
		      $("#hospitalName").val($("div[class='proSelect'] input").val());
		    }
		  	$("div[class='provinceHospital']").hide();
		  }
		});
	  });