mapper.delete:
delete from 
  m_tb33_unit_administrator_info
where
  USER_ID = #{user_id}
and 
  MANAGE_CALIBER_ID = #{manage_caliber_id}

mapper.select:
SELECT M.PERSONNEL_ID, M.PERSONNEL_NM, M.PERSONNEL_EMAIL, M.PERSONNEL_TEL, M1.POSITION_NM FROM m_tb12_personnel M LEFT JOIN ( SELECT * FROM m_tb17_personnel_job_info WHERE APPOINT_TIME IN( SELECT MAX(APPOINT_TIME) FROM m_tb17_personnel_job_info GROUP BY PERSONNEL_ID ))M1 ON M.PERSONNEL_ID = M1.PERSONNEL_ID WHERE M.PERSONNEL_UNIT = #{unit_no} AND M.DEL_KBN = 0
AND M.PERSONNEL_OFFICE = #{section_id}
LIMIT #{pageNo},#{pageCount}
